script:
require(["jquery", "owlcarousel"], function ($) {
    $(document).ready(function () {
        $(".graduate-carousel .avc").owlCarousel({
            autoplay: true,
            autoplayTimeout: 5000, // Adjust autoplay timeout as needed
            center: true,
            items: 3,
            margin: 30,
            loop: true,
            dots: true,
            navigation: true,
            responsiveClass: true,
            responsive: {
                0: {
                    items: 1,
                    margin: 10, // Set your margin for smaller screens here
                },
                600: {
                    items: 1,
                    margin: 20, // Set your margin for medium screens here
                },
                1200: {
                    items: 3,
                    margin: 30, // Set your margin for larger screens here
                },
            },
            navigationText: [
                '',
                '',
            ],
        });
    });
});
